// Login is the resolver for the login field.
func (r *mutationResolver) Login(ctx context.Context, email string, password string) (*model.AuthPayload, error) {
	var u model.User
	err := r.DB.Conn.QueryRow(ctx, "SELECT id, name, email, password FROM users WHERE email=$1", email).
		Scan(&u.ID, &u.Name, &u.Email, &u.Password)
	if err != nil {
		return nil, fmt.Errorf("no user found with email %s", email)
	}

	if err := bcrypt.CompareHashAndPassword([]byte(u.Password), []byte(password)); err != nil {
		return nil, fmt.Errorf("invalid password")
	}

	token, err := generateJWT(u.ID)
	if err != nil {
		return nil, err
	}

	u.Password = ""

	return &model.AuthPayload{
		User:  &u,
		Token: token,
	}, nil
}

// UploadFile is the resolver for the uploadFile field.
func (r *mutationResolver) UploadFile(ctx context.Context, userID string, file graphql.Upload) (*model.File, error) {
	// Create user folder if not exists
	folder := fmt.Sprintf("./uploads/user_%s", userID)
	if err := os.MkdirAll(folder, os.ModePerm); err != nil {
		return nil, err
	}

	// Save file locally
	outPath := fmt.Sprintf("%s/%s", folder, file.Filename)
	outFile, err := os.Create(outPath)
	if err != nil {
		return nil, err
	}
	defer outFile.Close()

	_, err = io.Copy(outFile, file.File)
	if err != nil {
		return nil, err
	}

	// Save metadata in DB
	var id int
	err = r.DB.Conn.QueryRow(ctx,
		"INSERT INTO files (user_id, filename, path) VALUES ($1, $2, $3) RETURNING id",
		userID, file.Filename, outPath,
	).Scan(&id)
	if err != nil {
		return nil, err
	}

	return &model.File{
		ID:         fmt.Sprintf("%d", id),
		Filename:   file.Filename,
		Path:       outPath,
		UploadedAt: time.Now().Format(time.RFC3339),
	}, nil
}

